    public int rand10() {
        while (true) {
            int num = (rand7() - 1) * 7 + rand7(); // Generate a number from 1 to 49
            if (num <= 40) { // Accept only if the number is in the range 1 to 40
                return 1 + (num - 1) % 10; // Map to the range 1 to 10
            }
        }
    }
